import React, { useEffect } from 'react'
import App from 'next/app'
import classNames from 'classnames'
import  '@style/index.less'
import Nav from '@root/components/Nav'
import CopyRight from '@root/components/CopyRight'

import { Provider } from 'react-redux'
import store from '@store/index'
import SiderWarp from '@root/components/SiderWarp'

class MyApp extends App {
  constructor (props: any) {
    super(props)
    this.state = {
      perspective: false
    }
  }

  public classWrapperString = classNames({
    [`react-next-wrapper`]: true,
    [`theme-default`]: true,
    ['perspective']: this.state.perspective
  })

  public showFn = () => {
    alert(1)
  }

  render() {
    const { Component, pageProps } = this.props

    return (
      <Provider store={store}>
        <div className={this.classWrapperString} id='dw-next-container'>
          <SiderWarp showFn={this.showFn}></SiderWarp>
          <div className='react-next-content'>
            <header>
              <Nav/>
            </header>
            <section>
              <Component {...pageProps} />
            </section>
            <CopyRight/>
          </div>
        </div>
      </Provider>
    )
  }
}

export default MyApp
