import React, { useEffect, useState } from 'react'
import cls from 'classnames'
import http from '@utils/req'
import { MUSIC_DETAIL } from '@constance/api'

const Music = () => {
  const [tracksLists, setTracksLists] = useState([])
  const classString = cls('music-sider-wrapper')

  useEffect(() => {
    getMusic(2179377798)
  }, [])

  const getMusic = async (id: number) => {
    const { playlist: { tracks } } = await http.get(`${MUSIC_DETAIL}?id=${id}`)
    setTracksLists(tracks)
  }

  return (
    <div className={classString}>
      <p className={`${classString}-title`}>音乐</p>
      <div className={`${classString}-content`}>
        {/* <li>111</li> */}
        {
          tracksLists && tracksLists.map((item) => (
          <li>{item.name}</li>
          ))
        }
      </div>
    </div>
  )
}

export default Music
