import React from 'react'
import { LightAsync as SyntaxHighlighter } from 'react-syntax-highlighter'
import js from 'react-syntax-highlighter/dist/cjs/languages/hljs/javascript';
// import jsx from 'react-syntax-highlighter/dist/cjs/languages/hljs/jsx';
import ts from 'react-syntax-highlighter/dist/cjs/languages/hljs/typescript';
// import tsx from 'react-syntax-highlighter/dist/cjs/languages/hljs/tsx';
// import 'prismjs/themes/prism-dark.css'
import '@style/high-custom.less'
import './code-block.less'

interface CodeBlockProps {
  language: string
  value: string
}

const CodeBlock: React.FC<CodeBlockProps> = ({ language, value }) => {
  SyntaxHighlighter.registerLanguage('javascript', js);
  // SyntaxHighlighter.registerLanguage('javascript', jsx);
  SyntaxHighlighter.registerLanguage('javascript', ts);
  // SyntaxHighlighter.registerLanguage('javascript', tsx);

  return (
    <figure className="highlight-block">
      <SyntaxHighlighter language=''
                         showLineNumbers={true}
                         useInlineStyles={false}>
        {value}
      </SyntaxHighlighter>
    </figure>
  )
}

export default CodeBlock
