import React, { useEffect } from 'react'
import classNames from 'classnames'
import './nav.less'
import {
  PROJECT_NAME,
  WEBSITE_TITLE
} from '@constance/index' 
import loadable from '@loadable/component'
import Link from 'next/link'
const Ripple = loadable(() => import('ripple-button'))

// import { useRipple } from 'ripple-button'
const Nav = () => {
  const classString = classNames({
    [`${PROJECT_NAME}-nav`]: true
  })

  useEffect(() => {
    const { useRipple } = require('ripple-button')
    useRipple(document.body, 'rgba(12, 232, 44, 0.1)')
  }, [])

  return (
    <nav className={ classString }>
      <div className="nav-content">
        <div className="nav-logo">
          <Link href="/blog">
            <a>
              <Ripple>
                <h2 className="nav-title">{WEBSITE_TITLE}</h2>
              </Ripple>
            </a>
          </Link>
        </div>
      </div>
    </nav>
  )
}

export default Nav
